import React, { useState } from "react";
import "./GymCard.css";
import ReviewPopUp from "../ReviewPopUp/ReviewPopUp";

interface GymCardProps {
    name: string;
    address: string;
    distance: number;
    rating: number;
    review: string;
}

const GymCard: React.FC<GymCardProps> = ({ name, address, distance, rating, review }) => {
    const [showPopup, setShowPopup] = useState(false);

    const handleReviewSubmit = (newRating: number, comment: string) => {
        console.log(`Gym: ${name}`);
        console.log(`Rating: ${newRating}`);
        console.log(`Comment: ${comment}`);
        alert(`Thanks for reviewing "${name}"!\nRating: ${newRating}/5\nComment: ${comment}`);
        setShowPopup(false);
    };

    return (
        <div className="gym-card">
            <div className="gym-card-header">
                <h3>{name}</h3>
                <p className="gym-card-rating">‚≠ê {rating}/5</p>
            </div>
            <p className="gym-card-address">{address}</p>
            <p className="gym-card-distance">üìç {distance} miles away</p>
            <p className="gym-card-review">"{review}"</p>
            <button className="leave-review-button" onClick={() => setShowPopup(true)}>
                Leave a Review
            </button>

            {showPopup && (
                <ReviewPopUp
                    onClose={() => setShowPopup(false)}
                    onSubmit={handleReviewSubmit}
                />
            )}
        </div>
    );
};

export default GymCard;
