import React from "react";
import Slider from "@mui/material/Slider";
import Typography from "@mui/material/Typography";

const FilterSlider: React.FC<{ onFilterChange: (filters: { radius: number; price: number; rating: number }) => void; }> = ({onFilterChange}) => {
    const [radius, setRadius] = React.useState(5);
    const [price,  setPrice] = React.useState(50);
    const [rating, setRating] = React.useState(3);

    const handleFilterChange = () => {
        onFilterChange({radius, price, rating});
    };

    return (
        <div className="filter-slider">
            <Typography gutterBottom>Search Radius (km)</Typography>
            <Slider 
            value={radius}
            min={1}
            max={50}
            onChange={(e, value) => setRadius(value as number)}
            onChangeCommitted={handleFilterChange}
            />

            <Typography gutterBottom>Price Range ($)</Typography>
            <Slider 
            value={price}
            min={10}
            max={200}
            onChange={(e, value) => setPrice(value as number)}
            onChangeCommitted={handleFilterChange}
            />

            <Typography gutterBottom>Rating:</Typography>
            <Slider
            value={rating}
            min={1}
            max={5}
            onChange={(e, value) => setRating(value as number)}
            onChangeCommitted={handleFilterChange}
            />
        </div>
    );
};

export default FilterSlider;
