import React from "react";
import "./GymCard.css";

interface GymCardProps {
    name: string;
    address: string;
    distance: number;
    rating: number;
    review: string;
    onLeaveReview: () => void;
}

const GymCard: React.FC<GymCardProps> = ({ name, address, distance, rating, review, onLeaveReview }) => {
    return (
        <div className="gym-card">
            <div className="gym-card-header">
                <h3>{name}</h3>
                <p className="gym-card-rating">‚≠ê {rating}/5</p>
            </div>
            <p className="gym-card-address">{address}</p>
            <p className="gym-card-distance">üìç {distance} miles away</p>
            <p className="gym-card-review">"{review}"</p>
            <button className="leave-review-button" onClick={onLeaveReview}>
                Leave a Review
            </button>
        </div>
    );
};

export default GymCard;
