import React, { useState } from "react";
import "./GymCard.css";
import ReviewDialog from "../ReviewDialog/ReviewDialog";

interface GymCardProps {
    name: string;
    address: string;
    distance: number;
    rating: number;
    review: string;
}

const GymCard: React.FC<GymCardProps> = ({ name, address, distance, rating }) => {
    const [isDialogOpen, setIsDialogOpen] = useState(false);

    const handleReviewSubmit = (review: string) => {
        console.log(`Review for ${name}: ${review}`);
        // You can implement API calls or state updates here
    };

    return (
        <div className="gym-card">
            <h3>{name}</h3>
            <p>{address}</p>
            <p>Distance: {distance} miles</p>
            <p>Rating: {rating}/5</p>
            <button onClick={() => setIsDialogOpen(true)} className="edit-button">
                Leave Review
            </button>
            <ReviewDialog
                isOpen={isDialogOpen}
                onClose={() => setIsDialogOpen(false)}
                onSubmit={handleReviewSubmit}
            />
        </div>
    );
};

export default GymCard;
