import React, { useState } from "react";
import "./ReviewDialog.css";

interface ReviewDialogProps {
    isOpen: boolean;
    onClose: () => void;
    onSubmit: (review: string) => void;
}

const ReviewDialog: React.FC<ReviewDialogProps> = ({ isOpen, onClose, onSubmit }) => {
    const [review, setReview] = useState("");

    if (!isOpen) return null; // Don't render if dialog is not open

    const handleSubmit = () => {
        if (review.trim()) {
            onSubmit(review);
            setReview(""); // Clear the review input
            onClose(); // Close the dialog
        }
    };

    return (
        <div className="review-dialog-overlay">
            <div className="review-dialog">
                <h3>Leave a Review</h3>
                <textarea
                    value={review}
                    onChange={(e) => setReview(e.target.value)}
                    placeholder="Write your review here..."
                    rows={5}
                />
                <div className="review-dialog-actions">
                    <button onClick={onClose} className="cancel-button">
                        Cancel
                    </button>
                    <button onClick={handleSubmit} className="submit-button">
                        Submit
                    </button>
                </div>
            </div>
        </div>
    );
};

export default ReviewDialog;
