import React, { useState, useEffect } from "react";
import ButtonGroup from "./ButtonGroups";
import GymCard, { GymCardProps } from "./GymCard";
import FilterSlider from "./FilterSlider";
import "../styles/HomePage.css";
import fetchGyms from "../utils/scraper";

const HomePage: React.FC = () => {
    // Typed state for gyms
    const [gyms, setGyms] = useState<GymCardProps[]>([]);
    // Typed state for filters
    const [filter, setFilters] = useState<{ radius: number; price: number; rating: number }>({
        radius: 10,
        price: 50,
        rating: 3,
    });

    // Function to fetch nearby gyms
    const handleNearbyGyms = () => {
        console.log("Fetching gyms near me...");
        // You can call `fetchGyms` here once it's integrated with the backend.
    };

    // Function to search gyms by location
    const handleSearchGyms = (location: string) => {
        console.log(`Searching gyms at ${location}`);
        // Placeholder: Add functionality to search gyms by location
    };

    // Function to update filters
    const handleFilterChange = (newFilter: { radius: number; price: number; rating: number }) => {
        setFilters(newFilter);
        console.log("Filters Updated:", newFilter);
    };

    return (
        <div className="homepage">
            <h1>Find the Best Gyms Near You</h1>
            {/* ButtonGroup for "Gyms Near Me" and Search */}
            <ButtonGroup onNearbyClick={handleNearbyGyms} onSearch={handleSearchGyms} />
            <div className="main-content">
                {/* FilterSlider for adjusting filters */}
                <FilterSlider onFilterChange={handleFilterChange} />
                {/* Displaying the list of gyms */}
                <div className="gym-cards">
                    {gyms.map((gym, index) => (
                        <GymCard key={index} {...gym} />
                    ))}
                </div>
            </div>
        </div>
    );
};

export default HomePage;
