import React, { useState } from "react";
import "./GymCard.css";
import ReviewPopUp from "../ReviewPopUp/ReviewPopUp";
import SuccessPopUp from "../SuccessPopUp/SuccessPopUp";

interface GymCardProps {
    name: string;
    address: string;
    distance: number;
    rating: number;
    review: string;
    setBlurBackground: (blur: boolean) => void;
}

const GymCard: React.FC<GymCardProps> = ({ name, address, distance, rating, review, setBlurBackground }) => {
    const [showReviewPopup, setShowReviewPopup] = useState(false);
    const [showSuccessPopup, setShowSuccessPopup] = useState(false);

    const handleReviewSubmit = (newRating: number, comment: string) => {
        console.log(`Gym: ${name}`);
        console.log(`Rating: ${newRating}`);
        console.log(`Comment: ${comment}`);
        setShowReviewPopup(false);
        setShowSuccessPopup(true);
        setBlurBackground(false);
        setTimeout(() => setShowSuccessPopup(false), 2000); // Close success popup after 2 seconds
    };

    const handleReviewPopupOpen = () => {
        setBlurBackground(true);
        setShowReviewPopup(true);
    };

    const handleReviewPopupClose = () => {
        setBlurBackground(false);
        setShowReviewPopup(false);
    };

    return (
        <>
            <div className="gym-card">
                <div className="gym-card-header">
                    <h3>{name}</h3>
                    <p className="gym-card-rating">‚≠ê {rating}/5</p>
                </div>
                <p className="gym-card-address">{address}</p>
                <p className="gym-card-distance">üìç {distance} miles away</p>
                <p className="gym-card-review">"{review}"</p>
                <button className="leave-review-button" onClick={handleReviewPopupOpen}>
                    Leave a Review
                </button>
            </div>

            {/* Review Popup */}
            {showReviewPopup && (
                <ReviewPopUp onClose={handleReviewPopupClose} onSubmit={handleReviewSubmit} />
            )}

            {/* Success Popup */}
            {showSuccessPopup && <SuccessPopUp message="Thanks for your review!" />}
        </>
    );
};

export default GymCard;
